ll mul(ll a,ll b,ll mod){
    //Assuming a,b,mod >=0; a,b<mod
    ll res=0;
    while(b){
        if(b&1){
            res=(res+a);
            if(res>=mod)
                res-=mod;
        }
        a=(a<<1);
        if(a>=mod){
            a%=mod;
        }
        b>>=1;
    }
    return res;
}
