snippet.text:
template <class T,class U> T fastexp(T a,U b){
    //Assumes b>=0
    T res = 1;
    while(b){
        if(b&1)
            res*=a;
        a*=a;
        b>>=1;
    }
    return res;
}

template <class T,class U,class V> T fastexp(T a,U b,V mod){
    //Assumes b>=0
    a%=mod;
    T res = 1;
    while(b){
        if(b&1)
            res= (res * a)%mod;
        a= (a*a)%mod;
        b>>=1;
    }
    return res;
}
